import React, { useState } from "react";
import "./team.css"

const Url = "http://127.0.0.1:3000/articles"
const Team = ({user, addArticle}) => {
  const [formData, setFormData] = useState({

    title: "",
    topic: "",
    story: "",
    time: ""
  })
  const [errors, setErrors] = useState([])

  //Update form data
  function handleChange(event) {
    const name = event.target.name;
    let value = event.target.value;

    setFormData({
      ...formData,
      [name]: value,
    });
  }

  //Handle artcile submit
  function handleSubmit() {
    // e.preventDefault()
    fetch(Url, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      }, 
      body: JSON.stringify(formData),
    }).then((res) => {
      if (res.ok) {
        res.json() 
        .then((article) => addArticle(article));
      } 
      else {
        res.json() 
        .then((err) => setErrors(err.errors));
      }
    });

    //Clear form
    setFormData({

      title: "",
      topic: "",
      story: "",
      time: ""    
    })
  }

  return (
    <>
    <div className="form-container">
        <form className="register-form" onSubmit={handleSubmit}>
          <select form="Categories" id="field" className="form-field" autofocus>
            <option value="red flags">Create a red flag</option>
            <option value="intervention">Create an intervention</option>
          </select>
          <input id="case-report" className="form-field" type="text" placeholder="Enter your report" name="report" onChange={handleChange} value={formData.title}/>
        <input id="desc-rption" className="form-field" type="text" placeholder="Description" name="description" onChange={handleChange} value={formData.topic}/>
          <input id="loc-tion" className="form-field" type="text" placeholder="Location" name="location" onChange={handleChange} value={formData.story} />
          <input id="da-te" className="form-field" type="text" placeholder="Date" name="date" onChange={handleChange} value={formData.time} />
          <label id="file">Choose an Image</label>
         <input id="image"className="form-field" type="file" placeholder="Image Url" name="image" />
          <button className="form-field" type="submit">SUBMIT</button>
        </form>
      </div>
     </> 
  );
};

export default Team;
