import React from "react";
import "./dashboard.css"
import { useNavigate } from 'react-router-dom'

const Redflags = ({article}) => {
  const { id, title, user, topic, created_at, story, time} = article;
  const history = useNavigate();

  function handleClick() {
    history(`/${id}`) 
  }


  return(
    <>
<div className="redflags">
<h3>Red flags</h3>
<img className='profile_image' src={user.profile_image} alt='DP' />
        <h4 className='username'>{user.username}</h4>
        <p className='date_created'>â€¢ {created_at.substring(0, 10)}</p>
      </div>
      <div className='content'>
        <h3 className='title'>{title}</h3>
        <p className='story'>{story}...</p>
      </div>
      <div className='btns'>
        <button className='btn'>{topic}</button>
        <button onClick={handleClick} className='btn'>{time}min read</button>

<button className="delete">delete</button>
<button className="delet">edit</button>
</div>
</>
  );
}
const Interventions = () => {
  return(
<>
<div className="intervene">
<h3>Interventions</h3>
<button className="delete">delete</button>
<button className="delet">edit</button>
</div>
</>
  );
}
const Dashboard = () => {

  return (
   <div>
<h2>Your Records</h2>
      <Redflags />
      <Interventions />
    </div>         
  );
};

export default Dashboard;
